import React from 'react';
import { Tabs, Button, Spin, Row, Col } from 'antd';
import { Gallery } from './Gallery';
import { CreatePostButton } from "./CreatePostButton";
import {
    GEOLOCATION_OPTIONS,
    POSITION_KEY,
    TOKEN_KEY,
    AUTH_HEADER,
    API_ROOT,
    POST_TYPE_IMAGE,
    POST_TYPE_VIDEO,
} from "../constants";
import '../styles/Home.css';

const { TabPane } = Tabs;

export class Home extends React.Component {
    state = {
        loadingGeolocation: false,
        loadingPosts: false,
        errorMessage: null,
        posts: [],
    };

    getGeolocation() {
        this.setState({
            loadingGeolocation: true,
            errorMessage: null,
        });

        //check if browser supports geolocaiton
        if ('geolocation' in navigator) {
            navigator.geolocation.getCurrentPosition(
                this.onGeolocationSuccess,
                this.onGeolocationFailure,
                GEOLOCATION_OPTIONS,
            );
        } else {
            this.setState({
                loadingGeolocation: false,
                errorMessage: 'Your browser does not support geolocation.',
            });
        }
    }

    onGeolocationSuccess = (position) => {
        this.setState({
            loadingGeolocation: false,
            errorMessage: null,
        });
        console.log(position);

        //destructuring
        const { latitude, longitude } = position.coords;
        //{ latitude: latitude, longitude: longitude }
        localStorage.setItem(POSITION_KEY, JSON.stringify({ latitude, longitude }));
        this.loadNearbyPost();
    }

    onGeolocationFailure = () => {
        this.setState({
            loadingGeolocation: false,
            errorMessage: 'Failed to load geolocation.',
        });
    }

    loadNearbyPost = () => {
        this.setState({
            loadingPosts: true,
            errorMessage: null,
        });

        const position = JSON.parse(localStorage.getItem(POSITION_KEY));
        const range = 20;
        const token = localStorage.getItem(TOKEN_KEY);

        fetch(`${API_ROOT}/search?lat=${position.latitude}&lon=${position.longitude}&range=${range}`, {
            method: 'GET',
            headers: {
                Authorization: `${AUTH_HEADER} ${token}`,
            }
        }).then((response) => {
            if (response.ok) {
                return response.json();
            }
            throw new Error('Fail to load posts');
        }).then((data) => {
            console.log(data);
            this.setState({
                loadingPosts: false,
                posts: data ? data : [],
            });
        }).catch((error) => {
            this.setState({
                loadingPosts: false,
                errorMessage: error.message,
            });
        })
    }

    getPosts(type) {
        if (this.state.errorMessage) {
            return <div>{this.state.errorMessage}</div>;
        } else if (this.state.loadingGeolocation) {
            return <Spin tip="Loading geolocation..."/>;
        } else if (this.state.loadingPosts) {
            return <Spin tip='Loading posts...'/>;
        } else if (this.state.posts.length > 0) {
            switch (type) {
                case POST_TYPE_IMAGE:
                    return this.getImagePosts();
                    break;
                case POST_TYPE_VIDEO:
                    return this.getVideoPosts();
                    break
                default:;
                    throw new Error('Unknown post type');
            }
        } else {
            return 'No nearby posts.';
        }
    }

    getImagePosts() {
        const images = this.state.posts
            .filter((post) => post.type == POST_TYPE_IMAGE)
            .map((post) => {
            return {
                src: post.url,
                thumbnail: post.url,
                thumbnailWidth: 400,
                thumbnailHeight: 300,
                caption: post.message,
                user: post.user,
            };
        });

        return (
            <Gallery images={images}/>
        );
    }

    getVideoPosts() {

    }

    componentDidMount() {
        this.getGeolocation();
    }

    render() {
        const operations = <CreatePostButton onSuccess={this.loadNearbyPost} />;

        return (
            <Tabs tabBarExtraContent={operations} className="main-tabs">
                <TabPane tab="Image Posts" key="1">
                    {this.getImagePosts()}
                </TabPane>
                <TabPane tab="Video Posts" key="2">
                    Video Posts
                </TabPane>
                <TabPane tab="Map" key="3">
                    Map
                </TabPane>
            </Tabs>
        );
    }
};
