    handleWebsiteChange = value => {
        let autoCompleteResult;
        if (!value) {
            autoCompleteResult = [];
        } else {
            autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
        }
        this.setState({ autoCompleteResult });
    }

    render () {
        // const { getFieldDecorator } = this.props.form;

        const formItemLayout = {
            labelCol: {
                xs: { span: 24 },
                sm: { span: 8 },
            },
            wrapperCol: {
                xs: { span: 24 },
                sm: { span: 16 },
            },
        };

        const tailFormItemLayout = {
            wrapperCol: {
                xs: {
                    span: 24,
                    offset: 0,
                },
                sm: {
                    span: 16,
                    offset: 8,
                },
            },
        };

        return (
            <Form {...formItemLayout} onFinish={this.handleSubmit} className="register-form">
                <Form.Item label="Username" name="username"
                   rules={[
                       {
                           required: true,
                           message: 'Please input your username!',
                       },
                ]}>
                    <Input />
                </Form.Item>

                <Form.Item label="Password" hasFeedback name="password"
                    rules={[
                        {
                            required: true,
                            message: 'Please input your password!',
                        },
                        // {
                        //     validator: this.validateToNextPassword,
                        // },
                ]}>
                    <Input.Password />
                </Form.Item>

                <Form.Item label="Confirm Password" hasFeedback name="confirm" dependencies={['password']}
                   rules={[
                       {
                           required: true,
                           message: 'Please confirm your password!',
                       },
                       ({ getFieldValue }) => ({
                           validator(rule, value) {
                               if (!value || getFieldValue('password') === value) {
                                   return Promise.resolve();
                               }

                               return Promise.reject('The two passwords that you entered do not match!');
                           },
                       }),
                       // {
                       //     validator: this.compareToFirstPassword,
                       // },
               ]}>
                    <Input.Password onBlur={this.handleConfirmBlur} />
                    {/*<Input.Password />*/}
                </Form.Item>

                <Form.Item {...tailFormItemLayout}>
                    <Button type="primary" htmlType="submit">
                        Register
                    </Button>
                    <div>
                        I already have an account, go back to <Link to="/login">login</Link>
                    </div>
                </Form.Item>
            </Form>
        );
    }

